import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';

const MyOrders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const navigate = useNavigate();

  const userInfo = useSelector((state) => state.userLogin?.userInfo);

  //   const userLogin = useSelector((state) => state.user);
  //   const { userInfo } = userLogin;

  useEffect(() => {
    // if (!userInfo) {
    //   navigate('/Beauty');
    //   return;
    // }

    const fetchOrders = async () => {
      try {
        const config = {
          headers: {
            Authorization: `Bearer ${userInfo.token}`,
          },
        };

        const { data } = await axios.get('/api/myorders', config);
        setOrders(data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch orders');
        setLoading(false);
      }
    };

    fetchOrders();
  }, [userInfo, navigate]);

  return (
  <div className="p-6 max-w-6xl mx-auto">
    <h2 className="text-3xl font-bold mb-6">ðŸ§¾ My Orders</h2>

    {loading ? (
      <p className="text-gray-500">Loading...</p>
    ) : error ? (
      <p className="text-red-600 font-semibold">{error}</p>
    ) : orders.length === 0 ? (
      <p className="text-gray-600">No orders yet.</p>
    ) : (
      <div className="overflow-x-auto">
        <table className="min-w-full border rounded-lg shadow-md bg-white">
          <thead className="bg-gray-100 text-gray-700 text-left text-sm uppercase">
            <tr>
              <th className="px-4 py-3">Order ID</th>
              <th className="px-4 py-3">Date</th>
              <th className="px-4 py-3">Total</th>
              <th className="px-4 py-3">Payment</th>
              <th className="px-4 py-3">Status</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order) => (
              <tr key={order._id} className="border-t hover:bg-gray-50 transition">
                <td className="px-4 py-3 text-blue-600 font-mono">{order._id}</td>
                <td className="px-4 py-3">{new Date(order.createdAt).toLocaleDateString()}</td>
                <td className="px-4 py-3 font-medium">â‚¹{order.totalPrice}</td>
                <td className="px-4 py-3">
                  <span
                    className={`px-2 py-1 rounded text-sm font-semibold ${
                      order.isPaid ? 'bg-green-100 text-green-700' : 'bg-yellow-100 text-yellow-700'
                    }`}
                  >
                    {order.isPaid ? 'Paid Online' : 'Pending'}
                  </span>
                </td>
                <td className="px-4 py-3 text-blue-600 font-semibold">
                  {order.status || 'Processing'}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    )}
  </div>
);

};

export default MyOrders;
