const savedCartState = JSON.parse(localStorage.getItem('cartState'));

const initialState = savedCartState || {
  cartItems: [],
  cartCounter: 0,
  totalPrice: 0,
  taxes: 0,
  deliverCharges: 50,
  grandTotal: 0,
};

// Function to calculate total price, taxes, and grand total
const calculateTotals = (cartItems, deliverCharges) => {
  const totalPrice = cartItems.reduce((acc, item) => acc + item.total_item_price, 0);
  const taxes = totalPrice * 0.18;
  const grandTotal = totalPrice + taxes + deliverCharges;

  return { totalPrice, taxes, grandTotal };
};

const cartReducer = (state = initialState, action) => {
  switch (action.type) {

    case 'ADD_TO_CART': {
      const productId = action.payload._id || action.payload.id;
      console.log("Adding to cart:", action.payload);
      console.log("Current cart items:", state.cartItems)
      // Normalize ID across all items
      const existIndex = state.cartItems.findIndex(item => item.id === productId);
    
      let updatedCartItems;
    
      if (existIndex !== -1) {
        // If product exists, update quantity and price
        updatedCartItems = state.cartItems.map((item, index) => {
          if (index === existIndex) {
            const newQty = item.qty + 1;
            return {
              ...item,
              qty: newQty,
              total_item_price: newQty * item.price,
            };
          }
          return item;
        });
      } else {
        // If product doesn't exist, add it
        const newItem = {
          ...action.payload,
          id: productId,         // normalize id
          qty: 1,
          total_item_price: action.payload.price,
        };
        updatedCartItems = [...state.cartItems, newItem];
      }
    
      const cartCounter = updatedCartItems.reduce((acc, item) => acc + item.qty, 0);
      const { totalPrice, taxes, grandTotal } = calculateTotals(updatedCartItems, state.deliverCharges);
    
      const updatedState = {
        ...state,
        cartItems: updatedCartItems,
        cartCounter,
        totalPrice,
        taxes,
        grandTotal,
      };
    
      localStorage.setItem('cartState', JSON.stringify(updatedState));
      return updatedState;
    }
    

    case 'INCREMENT_CART_COUNTER': {
      const updatedCartItems = state.cartItems.map(item => {
        if (item.id === action.payload) {
          const newQty = item.qty + 1; // Change quantity to qty
          return {
            ...item,
            qty: newQty, // Change quantity to qty
            total_item_price: newQty * item.price,
          };
        }
        return item;
      });

      const { totalPrice, taxes, grandTotal } = calculateTotals(updatedCartItems, state.deliverCharges);

      const updatedState = {
        ...state,
        cartItems: updatedCartItems,
        totalPrice,
        taxes,
        grandTotal,
      };

      localStorage.setItem('cartState', JSON.stringify(updatedState));
      return updatedState;
    }

    case 'DECREMENT_CART_COUNTER': {
      const updatedCartItems = state.cartItems.map(item => {
        if (item.id === action.payload && item.qty > 1) {
          const newQty = item.qty - 1;
          return {
            ...item,
            qty: newQty,
            total_item_price: newQty * item.price,
          };
        }
        return item;
      });

      const { totalPrice, taxes, grandTotal } = calculateTotals(updatedCartItems, state.deliverCharges);

      const updatedState = {
        ...state,
        cartItems: updatedCartItems,
        totalPrice,
        taxes,
        grandTotal,
      };

      localStorage.setItem('cartState', JSON.stringify(updatedState));
      return updatedState;
    }

    case 'LOAD_CART_FROM_DB': {
      const cartItems = action.payload;  // Loaded from the backend or localStorage
      const { totalPrice, taxes, grandTotal } = calculateTotals(cartItems, state.deliverCharges);
      const cartCounter = cartItems.reduce((acc, item) => acc + item.qty, 0);
    
      const updatedState = {
        ...state,
        cartItems,
        cartCounter,
        totalPrice,
        taxes,
        grandTotal,
      };
    
      localStorage.setItem('cartState', JSON.stringify(updatedState));  // Store the loaded state in localStorage
      return updatedState;
    }
    


    case 'CLEAR_CART': {
      // Clear localStorage when cart is cleared
      localStorage.removeItem('cartState');
      return {
        ...state,
        cartItems: [],
        cartCounter: 0,
        totalPrice: 0,
        taxes: 0,
        deliverCharges: 0,
        grandTotal: 0,
      };
    }

    default:
      return state;
  }
};

export default cartReducer;
