import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import Navbar1 from "../components/Navbar1";
import Footer from "../components/Footer";
import { loadStripe } from '@stripe/stripe-js';
import { incrementCartCounter, decrementCartCounter } from "../actions/cartActions";
import { saveUserCart } from "../actions/cartActions";
import axios from 'axios';

function CartDetails() {
    const dispatch = useDispatch();

    const cart = useSelector((state) => state.cart);  // entire cart state
    const { userInfo } = useSelector((state) => state.userLogin);  // user login info
    const cartItems = cart.cartItems;
    const cartCounter = cart.cartCounter;
    const totalPrice = cart.totalPrice;
    const deliveryCharges = cart.deliverCharges;
    const taxes = cart.taxes;
    const grandTotal = cart.grandTotal;

    const [detailedCartItems, setDetailedCartItems] = useState([]);

    // Save cart whenever it changes
    useEffect(() => {
        const fetchCartProductDetails = async () => {
            if (userInfo && cartItems.length > 0) {
                try {
                    const responses = await Promise.all(
                        cartItems.map((item) =>
                            axios.get(`http://localhost:7000/getproduct/${item.productId}`)
                        )
                    );

                    const enrichedItems = cartItems.map((item, index) => {
                        const product = responses[index].data;
                        const total_item_price = product.price * item.quantity;

                        return {
                            ...product,
                            quantity: item.quantity,
                            total_item_price,
                        };
                    });

                    setDetailedCartItems(enrichedItems);

                    // Save to backend (optional)
                    dispatch(saveUserCart(cart));
                } catch (err) {
                    console.error("Failed to fetch cart product details:", err);
                }
            } else {
                setDetailedCartItems([]);
            }
        };

        fetchCartProductDetails();
    }, [cartItems, userInfo, dispatch]);

    // Increment and decrement of cart items
    const handleIncrement = (id) => {
        dispatch(incrementCartCounter(id))
    }

    const handleDecrement = (id) => {
        dispatch(decrementCartCounter(id))
    }

    // Payment gateway
    const makePayment = async () => {
        const stripe = await loadStripe("pk_test_51RI9HhCH7rbrdLiTUHCfQv9xy3UM2E6JR5Y76pfXG9AiwjF77VIw5QdauuYRhqHejVRze2DqYKGUAwhdOjSsRZqA00amF3m3fD");

        const body = {
            products: cartItems
        };

        const headers = {
            "Content-Type": "application/json"
        };

        const response = await fetch("http://localhost:7000/api/create-checkout-session", {
            method: "POST",
            headers: headers,
            body: JSON.stringify(body)
        });

        const session = await response.json();

        const result = stripe.redirectToCheckout({
            sessionId: session.id,
        });

        if ((await result).error) {
            alert((await result).error.message);
        }
    };

    return (
        <div>
            <div className=" bg-white mt-1 ">
                <div className="cart-nav"> < Navbar1 /></div>
                {detailedCartItems.length ? 
                    <div className="row mt-5">
                        <h3>Product Details</h3>
                        <div className="col-md-8 shadow">
                            <div className="row border-bottom py-3">
                                <div className="col-md-8"> Item</div>
                                <div className="col-md-2"> Cost </div>
                                <div className="col-md-1"> Qty </div>
                                <div className="col-md-1">Total </div>
                            </div>

                            <div className="container">
                                {detailedCartItems.map(item => 
                                    <div className="row borderp-2 py-4" key={item.productId}>
                                        <div className="col-md-8 d-flex">
                                            <img src={item.image} alt={item.title} style={{ width: "40px", height: "40px" }} />
                                            <h6 className="ps-3">{item.title}</h6>
                                        </div>

                                        <div className="col-md-1">
                                            <p className="text-end">{item.price}</p>
                                        </div>

                                        <div className="col-md-2">
                                            <p className="text-end"><button className="cart-btns" onClick={() => handleDecrement(item.productId)}>-</button> {item.quantity} <button className="cart-btns" onClick={() => handleIncrement(item.productId)}>+</button></p>
                                        </div>

                                        <div className="col-md-1">
                                            <p className="text-end">{item.total_item_price.toFixed(2)}</p>
                                        </div>
                                    </div>
                                )}

                                <hr></hr>

                                <div className="row mb-2 py-3 pe-3">
                                    <div className="offset-md-9 col-md-1">
                                        <h5 className="text-end"> Total</h5>
                                    </div>

                                    <div className="col-md-1">
                                        <h5 className="text-end"> {cartCounter}</h5>
                                    </div>

                                    <div className="col-md-1">
                                        <h5 className="text-end"> {totalPrice.toFixed(2)}</h5>
                                    </div>
                                </div>
                            </div>
                        </div>

                        <div className="col-md-4">
                            <div className="shadow p-2 mx-2 pb-5">
                                <div className="d-flex justify-content-between px-2">
                                    <p>Sub Total</p> <p>{totalPrice.toFixed(2)}</p>
                                </div>

                                <div className="d-flex justify-content-between px-2">
                                    <p>Delivery Charges</p> <p>{deliveryCharges.toFixed(2)}</p>
                                </div>

                                <div className="d-flex justify-content-between px-2">
                                    <p>Tax</p> <p>{taxes.toFixed(2)}</p>
                                </div>

                                <div className="d-flex justify-content-between px-2">
                                    <p>Total</p> <p>{grandTotal.toFixed(2)}</p>
                                </div>

                                <div className="m-2">   
                                    <button onClick={makePayment} className="btn btn-primary">Proceed to Checkout</button> 
                                </div>
                            </div>
                        </div>
                    </div>
                :
                    <div className="empty-cart">
                        <img src="https://media.istockphoto.com/id/1352766190/vector/shopaholic-woman-running-with-an-empty-shopping-cart.jpg?s=612x612&w=0&k=20&c=2f0fEX7DqmYbkgY1l5w2-GLwCmLMTFLASnW-UyiPaX4=" 
                            alt="" width="250" height="250" />
                        <h1>Your cart is empty</h1>
                        <p>Just relax, let us help you find some first-class products</p>
                        <Link to='/'><button> Start Shopping</button></Link>
                    </div>
                }
            </div>
            <div><Footer /></div>
        </div>
    );
}

export default CartDetails;
