import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';

const MyOrders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const navigate = useNavigate();

  const userInfo = useSelector((state) => state.userLogin?.userInfo);

  //   const userLogin = useSelector((state) => state.user);
  //   const { userInfo } = userLogin;

  useEffect(() => {
    // if (!userInfo) {
    //   navigate('/Beauty');
    //   return;
    // }

    const fetchOrders = async () => {
      try {
        const config = {
          headers: {
            Authorization: `Bearer ${userInfo.token}`,
          },
        };

        const { data } = await axios.get('/api/myorders', config);
        setOrders(data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch orders');
        setLoading(false);
      }
    };

    fetchOrders();
  }, [userInfo, navigate]);

 return (
  <div className="p-6 max-w-5xl mx-auto">
    <h2 className="text-3xl font-bold mb-6">ðŸ§¾ My Orders</h2>

    {loading ? (
      <p className="text-gray-500">Loading...</p>
    ) : error ? (
      <p className="text-red-600 font-semibold">{error}</p>
    ) : orders.length === 0 ? (
      <p className="text-gray-600">No orders yet.</p>
    ) : (
      <div className="space-y-6">
        {orders.map((order) => (
          <div key={order._id} className="border rounded-lg shadow-md p-4 bg-white">
            <div className="flex justify-between items-center mb-4">
              <div>
                <p className="text-sm text-gray-500">Order ID: <span className="text-blue-700 font-mono">{order._id}</span></p>
                <p className="text-sm text-gray-500">Date: {new Date(order.createdAt).toLocaleDateString()}</p>
              </div>
              <div className="text-right">
                <p className="text-lg font-semibold">Total: â‚¹{order.totalPrice}</p>
                <p className={`text-sm font-medium ${order.isPaid ? 'text-green-600' : 'text-yellow-600'}`}>
                  {order.isPaid ? 'âœ… Payment Done' : 'âŒ› Pending Payment'}
                </p>
                <p className="text-sm text-blue-600">{order.status || 'Processing'}</p>
              </div>
            </div>

            <div className="border-t pt-4">
              <p className="font-semibold mb-2 text-gray-700">Items:</p>
              <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                {order.orderItems.map((item, idx) => (
                  <div key={idx} className="flex items-center gap-4 border p-3 rounded-lg hover:shadow-sm transition">
                    <img src={item.image} alt={item.name} className="w-16 h-16 object-cover rounded-md" />
                    <div>
                      <p className="font-semibold">{item.name}</p>
                      <p className="text-sm text-gray-600">Qty: {item.qty} Ã— â‚¹{item.price}</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        ))}
      </div>
    )}
  </div>
);

};

export default MyOrders;
