import axios from "axios";

// ==== Action Creators ====

// Save the user's cart to backend
export const saveUserCart = () => async (dispatch, getState) => {
  const { cart: { cartItems }, userLogin: { userInfo } } = getState();

  if (!userInfo) return;

  try {
    await axios.post("/api/cart/save", { cartItems }, {
      headers: { Authorization: `Bearer ${userInfo.token}` },
    });
  } catch (error) {
    console.error("Error saving cart to backend:", error);
  }
};

// Load the user's cart from backend
export const loadUserCart = (userInfo) => async (dispatch) => {
  if (!userInfo) return;

  try {
    console.log("📦 Fetching cart for user:", userInfo.token); // debugging
    const { data } = await axios.get("http://localhost:7000/api/cart", {
      headers: {
        Authorization: `Bearer ${userInfo.token}`,
      },
    });

    dispatch({ type: 'LOAD_CART_FROM_DB', payload: data });
  } catch (error) {
    console.error("❌ Failed to load user cart:", error);
  }
};


// ==== Cart Manipulation Actions ====


// Add item to cart

// Add item to cart
export const addToCart = (item) => async (dispatch) => {
  dispatch({ type: 'ADD_TO_CART', payload: item });
  dispatch(saveUserCart()); // Save updated cart
};


// Remove item from cart
export const removeFromCart = (item) => async (dispatch) => {
  dispatch({ type: 'REMOVE_FROM_CART', payload: item });
  dispatch(saveUserCart()); // Save updated cart
};

// Increment item quantity
export const incrementCartCounter = (id) => async (dispatch) => {
  dispatch({ type: 'INCREMENT_CART_COUNTER', payload: id });
  dispatch(saveUserCart()); // Save updated cart
};

// Decrement item quantity
export const decrementCartCounter = (id) => async (dispatch) => {
  dispatch({ type: 'DECREMENT_CART_COUNTER', payload: id });
  dispatch(saveUserCart()); // Save updated cart
};


// Clear the cart both locally and in the backend
export const clearCart = () => async (dispatch, getState) => {
  const { userLogin: { userInfo } } = getState();

  // If the user is logged in, clear the cart from the backend
  if (userInfo) {
    try {
      await axios.post("/api/cart/clear", {}, {
        headers: { Authorization: `Bearer ${userInfo.token}` },
      });
    } catch (error) {
      console.error("Error clearing cart in the backend:", error);
    }
  }

  // Dispatch action to clear cart in the Redux store
  dispatch({ type: 'CLEAR_CART' });
};
