import axios from "axios";
// ==

export const addToCart = (item) =>({
    type :'ADD_TO_CART',
    payload: item
});


export const removeFromCart = (item) =>({
    type :'REMOVE_FROM_CART',
    payload: item
});


export const incrementCartCounter = (id) =>({
    type :'INCREMENT_CART_COUNTER',
    payload: id
});


export const decrementCartCounter = (id) =>({
    type :'DECREMENT_CART_COUNTER',
    payload: id
   
});

// ===



export const saveUserCart = (cartState) => async (dispatch, getState) => {
  const { userLogin: { userInfo } } = getState();

  if (!userInfo) return;

  try {
    await axios.post("/api/cart/save", cartState, {
      headers: { Authorization: `Bearer ${userInfo.token}` }
    });
  } catch (err) {
    console.error("Cart save error", err);
  }
};

export const loadUserCart = () => async (dispatch, getState) => {
    const { userLogin: { userInfo } } = getState();
  
    try {
      const { data } = await axios.get("/api/cart", {
        headers: { Authorization: `Bearer ${userInfo.token}` }
      });
  
      if (data) {
        dispatch({ type: 'LOAD_CART_FROM_DB', payload: data });
        // localStorage.setItem('cartState', JSON.stringify(data)); // ✅ now here
      }
      
    } catch (err) {
      console.error("Cart load error", err);
    }
  };
