import React, { useEffect } from "react";
import { useDispatch } from "react-redux";
import { clearCart } from "../actions/cartActions";
import { Link } from "react-router-dom";

export default function Success() {
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(clearCart());  // Clear Redux + MongoDB cart after successful payment
  }, [dispatch]);

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-green-50 text-green-900">
      <div className="bg-white p-6 rounded-lg shadow-lg text-center">
        <h1 className="text-3xl font-bold mb-2">ðŸŽ‰ Payment Successful!</h1>
        <p className="text-lg mb-4">Thank you for your purchase.</p>
        <Link to="/" className="inline-block bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 transition">
          Continue Shopping
        </Link>
      </div>
    </div>
  );
}
