import React, { useEffect, useState } from "react";
import './ProductDetails.css';
import { useDispatch, useSelector } from 'react-redux';
import { addToCart, saveUserCart } from '../actions/cartActions';
import Swal from "sweetalert2";
import { Link, useParams, useNavigate } from "react-router-dom";
import Footer from "./Footer";

const ProductDetails = () => {
    const { id } = useParams();
    const [product, setProduct] = useState(null);
    const [rating, setRating] = useState(0);
    const [comment, setComment] = useState('');
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const userInfo = useSelector((state) => state.userLogin.userInfo);

    // Fetch product data
    const fetchProduct = async () => {
        try {
            const res = await fetch(`http://localhost:7000/allproducts/${id}`);
            const data = await res.json();
            setProduct(data);
        } catch (err) {
            console.error("Failed to fetch product:", err);
        }
    };

    useEffect(() => {
        fetchProduct();
    }, [id]);

    const handleCart = () => {
        if (!userInfo || !localStorage.getItem('auth-token')) {
            navigate('/Beauty');
            return;
        }

        const cartItem = {
            productId: product._id,
            title: product.title,
            price: product.price,
            image: product.image,
            qty: 1,
        };

        dispatch(addToCart(cartItem));
        dispatch(saveUserCart());

        Swal.fire({
            title: "Item added to cart!",
            icon: "success",
            draggable: true
        });
    };

    const submitReview = async (e) => {
        e.preventDefault();

        if (!rating || !comment) {
            Swal.fire("Please provide a rating and comment");
            return;
        }

        try {
            const res = await fetch(`http://localhost:7000/api/products/${product._id}/reviews`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${localStorage.getItem('auth-token')}`,
                },
                body: JSON.stringify({ rating, comment }),
            });

            const data = await res.json();

            if (res.ok) {
                Swal.fire("Review submitted!", "", "success");
                setRating(0);
                setComment('');
                fetchProduct(); // Refresh product with updated reviews
            } else {
                Swal.fire("Error", data.message, "error");
            }
        } catch (err) {
            Swal.fire("Error", "Failed to submit review", "error");
        }
    };

    return (
        <div>
            <div className="product-detail">
                <div className="product-images">
                    <img src={product?.image} alt="Main Product" className="main-image" />
                </div>

                <div className="product-info">
                    <h1>{product?.title}</h1>
                    <p className="price">&#8377;{product?.price}</p>

                    <div className="product-discription">
                        <h2>Product Details</h2>
                        <div className="reviews1">
                            <p>⭐ {product?.averageRating?.toFixed(1) || "No rating yet"}</p>
                            <p>({product?.reviews?.length || 0} reviews)</p>
                        </div>
                        <hr />
                    </div>

                    <div className="actions_wrraper">
                        <div className="actions">
                            {product?.quantity === 0 ? (
                                <button className="add-to-cart" disabled style={{ backgroundColor: 'gray', cursor: 'not-allowed' }}>
                                    Out of Stock
                                </button>
                            ) : (
                                <button className="add-to-cart" onClick={handleCart}>
                                    <i className="fa-solid fa-cart-shopping"></i> Add to Cart
                                </button>
                            )}
                            <Link to="/Cart">
                                <button className="buy-now"><i className="fa-solid fa-angles-right"></i> Buy Now</button>
                            </Link>
                        </div>
                    </div>

                    <div className="additional-info">
                        <p><strong>Free Shipping on Orders Over &#8377;50</strong></p>
                        <p><strong>24/7 Customer Support:</strong> +1-800-123-4567</p>
                        <p><strong>Delivery:</strong> 3 - 5 Business Days</p>

                        {product?.reviews?.length > 0 && (
                            <div className="product-reviews-section">
                                <h2>Customer Reviews</h2>
                                {product.reviews.map((rev, index) => (
                                    <div key={index} className="single-review">
                                        <p>
                                            <strong>{rev.user?.name || rev.user?.email || "Anonymous"}:</strong> ⭐ {rev.rating}
                                        </p>
                                        <p>{rev.comment}</p>
                                        <hr />
                                    </div>
                                ))}
                            </div>
                        )}
                    </div>

                    {userInfo ? (
                        <div className="review-section">
                            <h2>Write a Review</h2>
                            <form onSubmit={submitReview} className="submit-review-section">
                                <select value={rating} onChange={(e) => setRating(Number(e.target.value))}>
                                    <option value={0}>Select Rating</option>
                                    <option value={1}>1 - Poor</option>
                                    <option value={2}>2 - Fair</option>
                                    <option value={3}>3 - Good</option>
                                    <option value={4}>4 - Very Good</option>
                                    <option value={5}>5 - Excellent</option>
                                </select>
                                <textarea
                                    placeholder="Write your review here..."
                                    value={comment}
                                    onChange={(e) => setComment(e.target.value)}
                                ></textarea>
                                <button type="submit">Submit Review</button>
                            </form>
                        </div>
                    ) : (
                        <p style={{ marginTop: '20px', color: 'gray' }}>Please log in to write a review.</p>
                    )}
                </div>
            </div>

            <div className="footer1">
                <Footer />
            </div>
        </div>
    );
};

export default ProductDetails;
