import axios from "axios";

// ==== Action Creators ====

export const saveUserCart = () => async (dispatch, getState) => {
  const {
    cart: { cartItems },
    userLogin: { userInfo }
  } = getState();

  console.log("🛒 saveUserCart triggered", { userInfo, cartItems });

  // ✅ Abort if user is not logged in or login failed
  if (!userInfo || userInfo.success === false) {
    console.warn("🚫 Aborting saveUserCart: user not logged in or login failed.");
    return;
  }

  try {
    // Transform cart items to match the backend structure
    const transformedCart = cartItems.map(item => ({
      productId: item._id,
      qty: item.qty <= 0 ? 1 : item.qty, // Ensure qty is at least 1
    }));

    // Validate that no item has an invalid quantity (<= 0)
    if (transformedCart.some(item => !item.productId || item.qty <= 0)) {
      console.error('❌ Invalid cart items:', transformedCart);
      return;
    }

    // Send the transformed cart data to the backend
    await axios.post(
      "http://localhost:7000/api/cart/save",
      { cartItems: transformedCart },
      {
        headers: { Authorization: `Bearer ${userInfo.token}` },
      }
    );

    console.log("✅ Cart saved:", transformedCart);
  } catch (error) {
    console.error("❌ Error saving cart to backend:", error);
  }
};

// Load the user's cart from the backend

export const loadUserCart = (userInfo) => async (dispatch) => {
  try {
    const { data } = await axios.get("http://localhost:7000/getcart", {
      headers: {
        Authorization: `Bearer ${userInfo.token}`,
      },
    });

    console.log("🛒 Loaded cart from DB:", data);

    dispatch({ type: "LOAD_CART_FROM_DB", payload: data.cartItems || [] });
  } catch (error) {
    console.error("❌ Failed to load user cart:", error);
  }
};

// ==== Cart Manipulation Actions ====

export const addToCart = (item) => async (dispatch) => {
  dispatch({ type: 'ADD_TO_CART', payload: item });
  dispatch(saveUserCart());
};

export const removeFromCart = (item) => async (dispatch) => {
  dispatch({ type: 'REMOVE_FROM_CART', payload: item });
  dispatch(saveUserCart());
};

export const incrementCartCounter = (id) => async (dispatch) => {
  dispatch({ type: 'INCREMENT_CART_COUNTER', payload: id });
  dispatch(saveUserCart());
};

export const decrementCartCounter = (id) => async (dispatch) => {
  dispatch({ type: 'DECREMENT_CART_COUNTER', payload: id });
  dispatch(saveUserCart());
};

export const clearCart = () => async (dispatch, getState) => {
  const { userLogin: { userInfo } } = getState();

  if (userInfo) {
    try {
      await axios.post("/api/cart/clear", {}, {
        headers: { Authorization: `Bearer ${userInfo.token}` },
      });
    } catch (error) {
      console.error("Error clearing cart in the backend:", error);
    }
  }

  dispatch({ type: 'CLEAR_CART' });
};
