import axios from "axios";
import { loadUserCart } from "./cartActions"; 

export const loginUser = (formData) => async (dispatch) => {
  try {
    const { data } = await axios.post("http://localhost:7000/login", formData, {
      headers: { "Content-Type": "application/json" },
    });

    dispatch({ type: "USER_LOGIN_SUCCESS", payload: data });

    // Save userInfo (with token) to localStorage
    localStorage.setItem('userInfo', JSON.stringify(data));

    // Load user cart from DB
    dispatch(loadUserCart());
  } catch (error) {
    console.error("Login error:", error);
  }
};


export const logout = () => (dispatch) => {
  localStorage.removeItem('userInfo');
  localStorage.removeItem('cartState');  //  clear cart from localStorage

  dispatch({ type: 'USER_LOGOUT' });
  dispatch({ type: 'CLEAR_CART' });       //  clear cart from Redux
};
