const savedCartState = JSON.parse(localStorage.getItem('cartState'));

const initialState = savedCartState || {
  cartItems: [],
  cartCounter: 0,
  totalPrice: 0,
  taxes: 0,
  deliverCharges: 50,
  grandTotal: 0,
};

// Function to calculate total price, taxes, and grand total
const calculateTotals = (cartItems, deliverCharges) => {
  const totalPrice = cartItems.reduce((acc, item) => acc + item.total_item_price, 0);
  const taxes = totalPrice * 0.18;
  const grandTotal = totalPrice + taxes + deliverCharges;

  return { totalPrice, taxes, grandTotal };
};

const cartReducer = (state = initialState, action) => {
  switch (action.type) {
    
    case 'ADD_TO_CART': {
      const existIndex = state.cartItems.findIndex(item => item.id === (action.payload.id || action.payload._id));
    
      let updatedState;
    
      if (existIndex !== -1) {
        // If item already exists in cart, update its qty and price
        const updatedCartItems = state.cartItems.map((item, index) => {
          if (index === existIndex) {
            const newQty = item.qty + 1;
            return {
              ...item,
              qty: newQty,
              total_item_price: newQty * item.price,
            };
          }
          return item;
        });
    
        updatedState = {
          ...state,
          cartItems: updatedCartItems,
          cartCounter: state.cartCounter + 1,
        };
      } else {
        // If item doesn't exist, add it to the cart
        updatedState = {
          ...state,
          cartItems: [
            ...state.cartItems,
            {
              ...action.payload,
              id: action.payload.id || action.payload._id,              qty: 1,
              total_item_price: action.payload.price,
            },
          ],
          cartCounter: state.cartCounter + 1,
        };
      }
    
      const { totalPrice, taxes, grandTotal } = calculateTotals(updatedState.cartItems, state.deliverCharges);
    
      updatedState = {
        ...updatedState,
        totalPrice,
        taxes,
        grandTotal,
      };
    
      localStorage.setItem('cartState', JSON.stringify(updatedState));
      return updatedState;
    }
    

    case 'INCREMENaT_CART_COUNTER': {
      const updatedCartItems = state.cartItems.map(item => {
        if (item._id === action.payload) {
          const newQty = item.qty + 1; // Change quantity to qty
          return {
            ...item,
            qty: newQty, // Change quantity to qty
            total_item_price: newQty * item.price,
          };
        }
        return item;
      });

      const { totalPrice, taxes, grandTotal } = calculateTotals(updatedCartItems, state.deliverCharges);

      const updatedState = {
        ...state,
        cartItems: updatedCartItems,
        totalPrice,
        taxes,
        grandTotal,
      };

      localStorage.setItem('cartState', JSON.stringify(updatedState));
      return updatedState;
    }

    case 'DECREMENT_CART_COUNTER': {
      const updatedCartItems = state.cartItems.map(item => {
        if (item._id === action.payload && item.qty > 1){ 
          const newQty = item.qty - 1; 
          return {
            ...item,
            qty: newQty, 
            total_item_price: newQty * item.price,
          };
        }
        return item;
      });

      const { totalPrice, taxes, grandTotal } = calculateTotals(updatedCartItems, state.deliverCharges);

      const updatedState = {
        ...state,
        cartItems: updatedCartItems,
        totalPrice,
        taxes,
        grandTotal,
      };

      localStorage.setItem('cartState', JSON.stringify(updatedState));
      return updatedState;
    }

    case 'LOAD_CART_FROM_DB': {
      const cartItems = action.payload;
      const { totalPrice, taxes, grandTotal } = calculateTotals(cartItems, state.deliverCharges);
      const cartCounter = cartItems.reduce((acc, item) => acc + item.qty, 0);
    
      const updatedState = {
        ...state,
        cartItems,
        cartCounter,
        totalPrice,
        taxes,
        grandTotal,
      };
    
      localStorage.setItem('cartState', JSON.stringify(updatedState));
      return updatedState;
    }
    
    

    case 'CLEAR_CART': {
      // Clear localStorage when cart is cleared
      localStorage.removeItem('cartState');
      return {
        ...state,
        cartItems: [],
        cartCounter: 0,
        totalPrice: 0,
        taxes: 0,
        grandTotal: 0,
      };
    }

    default:
      return state;
  }
};

export default cartReducer;
