import React, { useEffect, useState } from "react";


const ListProduct = () =>{

    const [allproducts,setAllProducts] = useState([]);
    const [updatedQuantity, setUpdatedQuantity] = useState({});

    const fetchInfo = async () =>{
        await fetch('http://localhost:7000/allproducts')
        .then((res)=>res.json())
        .then((data)=>{setAllProducts(data)});
    }

    useEffect(()=>{
        fetchInfo();
    },[])

    const remove_product = async (id) =>{
        await fetch('http://localhost:7000/removeproduct',{
            method:'POST',
            headers:{
                Accept:'application/json',
                'Content-Type':'application/json',

            },
            body:JSON.stringify({id:id})
        })

        await fetchInfo();
    }

    const updateProductQuantity = async (id) => {
        const quantity = updatedQuantity[id]; // Get the updated quantity for this product
        if (quantity) {
          await fetch(`http://localhost:7000/vendor/update-quantity/${id}`, {
            method: 'PUT',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({ quantity }),
          });
          await fetchInfo();  // Reload the list of products after update
        }
      }


    return(
        <div className="list-product">
            <h1>All Products List</h1>

            <div className="listproduct-format-main">
                <p>Products</p> 
                <p>Title</p> 
                <p>Price</p> 
                <p>Category</p> 
                <p>Rating</p> 
                <p>Reviews</p> 
                <p>Quantity</p> 
                <p>Remove</p> 
                <p>Update Quantity</p>


            </div>

            <div className="listproduct-allproducts">

                <hr/>
                {allproducts.map((product,index)=>{
                     return <> <div key={index} className="listproduct-format-main listproduct-format"> 
                         
                         <img className="listproduct-product-icon" src={product.image} alt=""/>
                         <p>{product.title}</p>
                         <p>${product.price}</p>
                         <p>{product.category}</p>
                         <p>{product.rating}</p>
                         <p>{product.reviews}</p>
                         <p>{product.quantity}</p>

                         <input 
                type="number" 
                value={updatedQuantity[product.id] || product.quantity} 
                onChange={(e) => setUpdatedQuantity({ ...updatedQuantity, [product.id]: e.target.value })} 
                min="0"
              />
              <button onClick={() => updateProductQuantity(product.id)}>Update</button>

                         <img onClick={()=>{remove_product(product.id)}} className="listproduct-remove-icon" src="https://img.freepik.com/premium-vector/reject-vector-icon-illustration-design_535345-7353.jpg?w=900" height={50} alt=""/>

                         </div>

                         <hr/>
                         </>
                })}

            </div>

        </div>

    )
}

export default ListProduct
